import React, {useState} from 'react';
import ReactDOM from 'react-dom'
import 'antd/dist/antd.css';
import '../index.css';
import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Rate,
    Checkbox,
    Row,
    Col, Input,
} from 'antd';

import {InboxOutlined} from '@ant-design/icons';
import {Option} from "antd/es/mentions";
import axios from "axios";
import {combine_results_api, uploadfile, wortwhile} from "./wortwhile";

const backend_folder = "/home/vuthaouet/blockchain/worthwhile-tool-smartcontract-backend/data/"
const baseUrl = "http://localhost:5000";
const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const normFile = (e) => {
    console.log('Upload event:', e);

    if (Array.isArray(e)) {
        return e;
    }

    return e && e.fileList;
};

async function submitForm(values) {

    let data_static = {}
    data_static["sourceLanguage"] = ""
    data_static["list_contract"] = []
    data_static["count_risk_of_false_positives"] = 0
    data_static["count_leve_vulnerabilities"]= {}
    data_static["count_vulnerabilities"] = {}
    const formData = new FormData();
    formData.append("type", values['type']);
    formData.append("project_name", values["project_name"]);
    formData.append("list_tool", values["list_tool"]);
    const data = {
        type: values['type'],
        project_name: values["project_name"]
    }
    values["file"].map((file) => {
        formData.append("file", file.originFileObj);
        // data.file ("file", file.originFileObj);
    });
    console.log("formData.values()")
    console.log(...formData)
    // let check = uploadfile(formData.)
    // let res = await axios.post(`${baseUrl}${'/wortwhile/uploadfile'}`, formData)
    //     .then((res) => {
    //         return {
    //             success: true,
    //             data: res.data,
    //         }
    //     })
    //     .catch((err) => alert("File Upload Error"));
    // if (res) {
    //     console.log(res)
    //     let check = await wortwhile(res.data)
    //     let data_static = await combine_results_api(values["project_name"])
    //     return data_static
    // }

    return data_static
}

function results(values) {
    console.log("results")
    console.log(values)
    console.log(values["list_contract"])
    let vulnerabilities = values["count_vulnerabilities"]
    const element = (
        <div>
            <h1>Synthesis Results</h1>
            <h2>List smartcontract: {values["list_contract"]}.</h2>
            <h2>Source Language: {values["sourceLanguage"]}.</h2>
            <h2>List vulnerabilities: {vulnerabilities.map(vulnerabilitie => <p>{vulnerabilitie}</p>)}.</h2>
            <h2>Number rick of false positives: {values["count_risk_of_false_positives"]}.</h2>
            <h2>List leve vulnerabilities: {values["count_leve_vulnerabilities"].map(level => <p>{level}</p>)}.</h2>


            {/*<p>List smartcontract: {values["list_contract"]}</p>*/}
            {/*<p>Source Language: {values["sourceLanguage"]}</p>*/}
            {/*<p>List vulnerabilities: {values["count_vulnerabilities"]}</p>*/}
            {/*<p>Number rick of flase positives: {values["count_risk_of_false_positives"]}</p>*/}
            {/*<p>List leve vulnerabilities: {values["count_leve_vulnerabilities"]}</p>*/}
            {/*<a href={"/home/vuthaouet/blockchain/worthwhile-tool-smartcontract-backend/data/thaovttest1/synthesis_results.json"} download>Click to download synthesis results</a>*/}
        </div>
    );
    ReactDOM.render(element, document.getElementById('results'));
}

class MyUpload extends React.Component {
    state = {
        loadings: [],
    };

    handleUpload = ({fileList}) => {
        console.log("vào handleUpload")
        //---------------^^^^^----------------
        // this is equivalent to your "const img = event.target.files[0]"
        // here, antd is giving you an array of files, just like event.target.files
        // but the structure is a bit different that the original file
        // the original file is located at the `originFileObj` key of each of this files
        // so `event.target.files[0]` is actually fileList[0].originFileObj
        console.log('fileList', fileList);

        // you store them in state, so that you can make a http req with them later
        this.setState({fileList});
    };
    onFinish = async (values) => {
        console.log("onFinish")
        console.log(values)
        console.log("vào onFinish")
        // this.handleSubmit
        let data_static = await submitForm(values)
        this.setState(({loadings}) => {
            const newLoadings = [...loadings];
            newLoadings[2] = false;

            return {
                loadings: newLoadings,
            };
        });
        results(data_static)
    };
    enterLoading = index => {
        this.setState(({loadings}) => {
            const newLoadings = [...loadings];
            newLoadings[index] = true;

            return {
                loadings: newLoadings,
            };
        });
        // setTimeout(() => {
        //     this.setState(({ loadings }) => {
        //         const newLoadings = [...loadings];
        //         newLoadings[index] = false;
        //
        //         return {
        //             loadings: newLoadings,
        //         };
        //     });
        // }, 12000);
    };

    render() {
        const {loadings} = this.state;
        return (
            <div>
                <Form
                    name="validate_other"
                    {...formItemLayout}
                    onFinish={this.onFinish}
                    enctype="multipart/form-data"
                    // onSubmit={this.handleSubmit}

                >
                    <Form.Item
                        {...formItemLayout}
                        name="project_name"
                        label="Your project name"
                        rules={[
                            {
                                required: true,
                                message: 'Please input your project name',
                            },
                        ]}
                    >
                        <Input placeholder="Please input your project name"/>
                    </Form.Item>
                    <Form.Item name="list_tool" label="Tool"
                               rules={[
                                   {
                                       required: true,
                                       message: 'Please choose tool',
                                   },
                               ]}>
                        <Checkbox.Group>
                            <Row>
                                <Col span={4}>
                                    <Checkbox
                                        value="mythril"
                                        style={{
                                            lineHeight: '32px',
                                        }}
                                    >
                                        Mythril
                                    </Checkbox>
                                </Col>
                                <Col span={8}>
                                    <Checkbox
                                        value="slither"
                                        style={{
                                            lineHeight: '32px',
                                        }}
                                    >
                                        Slither
                                    </Checkbox>
                                </Col>
                                <Col span={8}>
                                    <Checkbox
                                        value="oyente"
                                        style={{
                                            lineHeight: '32px',
                                        }}
                                    >
                                        Oyente
                                    </Checkbox>
                                </Col>
                                <Col span={8}>
                                    <Checkbox
                                        value="honeybadger"
                                        style={{
                                            lineHeight: '32px',
                                        }}
                                    >
                                        Honeybadger
                                    </Checkbox>
                                </Col>

                            </Row>
                        </Checkbox.Group>
                    </Form.Item>
                    <Form.Item
                        name="type"
                        label="type"
                        hasFeedback
                        rules={[
                            {
                                required: true,
                                message: 'Please input your type',
                            },
                        ]}
                    >
                        <Select placeholder="Please input your type">
                            <Option value="file">File</Option>
                            <Option value="repo">Repo</Option>
                        </Select>
                    </Form.Item>
                    <Form.Item
                        {...formItemLayout}
                        name="linkRepo"
                        label="Link Repo"
                    >
                        <Input placeholder="Please input link repo"/>
                    </Form.Item>
                    <Form.Item label="file">
                        <Form.Item name="file" valuePropName="fileList" getValueFromEvent={normFile} noStyle>
                            <Upload.Dragger name="files" beforeUpload={() => false} fileList={normFile}
                                            onChange={this.handleUpload}>
                                <p className="ant-upload-drag-icon">
                                    <InboxOutlined/>
                                </p>
                                <p className="ant-upload-text">Click or drag file to this area to upload</p>
                                <p className="ant-upload-hint">Support for a single or bulk upload.</p>
                            </Upload.Dragger>
                        </Form.Item>
                    </Form.Item>

                    <Form.Item
                        wrapperCol={{
                            span: 12,
                            offset: 6,
                        }}
                    >
                        <Button type="primary" htmlType="submit" loading={loadings[2]}
                                onClick={() => this.enterLoading(2)}>
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
                <div id={"results"}>
                </div>
            </div>
        );
    }
};

export default MyUpload
